import { Pipe, PipeTransform } from '@angular/core';
import { Dish } from 'src/dish';

@Pipe({
  name: 'searchPipe',
  pure:false
})
export class SearchPipe implements PipeTransform {

  transform(Dishes:Dish[],filterString:string) {

      console.log('filter pipe called');

      if(Dishes.length===0 || filterString===''){

       return Dishes;

      }else{

       return Dishes.filter((pln)=>{return pln.dishName.toLowerCase()===filterString.toLowerCase() || pln.dishName.toLowerCase()===filterString.toLowerCase() })

      }

     }

}
