import { Component, OnInit } from '@angular/core';
import { DispDishService } from './disp-dish.service';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-disp-dishes',
  templateUrl: './disp-dishes.component.html',
  styleUrls: ['./disp-dishes.component.css'],
  providers:[DispDishService]
})
export class DispDishesComponent implements OnInit {

    dishes:any;
    id:any;

    constructor (private client : DispDishService,private route:ActivatedRoute){

    }
    deleteDish(id: number){
      this.client.deletedish(id).subscribe(res=>{
        console.log(res);
        this.fetchDish();

      })
    }
    fetchDish(){
      this.client.getdish(this.id).subscribe(res=>{
        this.dishes=res;
      });
    }
    ngOnInit(): void {
        this.route.params.subscribe(res=>{
        this.id=res["name"];
        })
        this.fetchDish();
        console.log(this.id);
    }
}
<div class="container">
  <div class="row mb-3">
    <div class="col-md-3">
      <div class="input-group">
        <input type="text" class="form-control" placeholder="Search">
        <div class="input-group-append">
          <button class="btn btn-outline-secondary" type="button">
            <i class="fa fa-search"></i>
          </button>
        </div>
      </div>
    </div>
  </div>

  <div class="grid-container">
    <div class="card" *ngFor="let dish of dishes">
      <img [src]="dish.dshImage" class="card-img-top" alt="dish image" />
      <div class="card-content">
        <h5 class="card-title">{{ dish.dishName }}</h5>
        <p class="card-description">{{ dish.dishDescription }}</p>
        <p class="card-price">{{ dish.dishPrice | currency }}</p>
        <p class="card-text">{{ dish.nature }}</p>
        <button class="btn btn-danger" (click)="deleteDish(dish.dishId)">Delete</button>
      </div>
    </div>
  </div>
</div>
