import { Component } from '@angular/core';
import { Router } from '@angular/router';

@Component({
  selector: 'app-login',
  templateUrl: './login.component.html',
  styleUrls: ['./login.component.css']
})
export class LoginComponent {
  username: string = '';
  password: string = '';

  constructor(private router: Router) {}

  onSubmit() {
    // Static validation for username and password
    if (this.username === 'user' && this.password === 'password') {
      // If login is successful, navigate to the home component
      this.router.navigate(['/home']);
    } else {
      // If login is unsuccessful, show an alert
      alert('Invalid credentials');
    }
  }
}
<h2>Login</h2>
<form>
  <div>
    <label for="username">Username:</label>
    <input type="text" id="username" name="username" [(ngModel)]="username">
  </div>
  <div>
    <label for="password">Password:</label>
    <input type="password" id="password" name="password" [(ngModel)]="password">
  </div>
  <button type="button" (click)="onLogin()">Login</button>
</form>
