import { Component, OnInit } from '@angular/core';
import { DispDishService } from './disp-dish.service';
import { ActivatedRoute } from '@angular/router';


@Component({
  selector: 'app-disp-dishes',
  templateUrl: './disp-dishes.component.html',
  styleUrls: ['./disp-dishes.component.css'],
  providers:[DispDishService]
})
export class DispDishesComponent implements OnInit {

    dishes:any;
    filtereddishes:any;
    id:any;
    constructor (private client : DispDishService,private route:ActivatedRoute){

    }
    deleteDish(id: number){
      this.client.deletedish(id).subscribe(res=>{
        console.log(res);
        this.fetchDish();

      })
    }
    fetchDish(){
      this.client.getdish(this.id).subscribe(res=>{
        this.dishes=res;
        this.filtereddishes=[...this.dishes];
      });
    }
    ngOnInit(): void {
        this.route.params.subscribe(res=>{
        this.id=res["name"];
        })
        this.fetchDish();
        console.log(this.id);
    }
    filterDish(evt:Event){
        const value:String=(evt.target as HTMLInputElement).value;
        console.log(value);
        this.filtereddishes=this.dishes.filter((dish:any)=>{
          return dish.dishName.includes(value);
        })
    }
}
<link rel="stylesheet" href="https://cdnjs.cloudflare.com/ajax/libs/font-awesome/4.7.0/css/font-awesome.min.css">

<div class="container">
  <div class="row mb-3">
    <div class="col-md-4">
      <div class="input-group">
        <input type="text" class="form-control rounded-pill" placeholder="Search" (change)="filterDish($event)" >
        <div class="input-group-append">
          <button class="btn btn-outline-secondary" type="button" data-toggle="tooltip" data-placement="bottom" title="Search">
            <i class="fa fa-search"></i>
          </button>
        </div>
      </div>
    </div>
  </div>
  <div class="row">
    <div class="col-md-3 mb-3" *ngFor="let dish of filtereddishes " >
      <div class="card h-100">
        <img [src]="dish.dshImage" class="card-img-top" alt="dish image" />
        <div class="card-body">
          <h5 class="card-title">{{ dish.dishName }}</h5>
          <p class="card-text">{{ dish.dishDescription }}</p>
          <p class="card-text">{{ dish.dishPrice | currency }}</p>
          <p class="card-text">{{ dish.nature }}</p>
          <button class="btn btn-danger" (click)="deleteDish(dish.dishId)">Delete</button>
          <button class="btn btn-outline-secondary" style="margin-left: 55px ;"  >Update</button>
        </div>
      </div>
    </div>
  </div>
</div>
